import React, { useEffect, useState } from 'react';
import FeedPost from './feedPost';
import axios from 'axios';
const Feeds = () => {

  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  const search = async () => {
    try {
      const response = await axios.post('http://localhost:3001/search/', { searchTerm });
      setSearchResults(response.data);
    } catch (error) {
      console.error('Error fetching search results:', error);
    }
  };
  const getQuestions=async()=>{
    try {
      const response = await fetch('http://localhost:3001/getQuestions/');
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      const data = await response.json();

      setQuestions(data);
    } catch (error) {
      console.error('Error fetching items:', error);
      setError('Error fetching items. Please try again later.');
    } finally {
      setLoading(false);
    }
  }
  useEffect(()=>{
    getQuestions();
  },[questions])
 return(
  <>
   {/* Question Search Box */}
   <div className='center-search-container'>
   <form onSubmit={(e) => { e.preventDefault(); search(); }}>
       <i className="fab fa-searchengin"></i>
       <input
         type="text"
         className='search-input'
         placeholder="Search a question"
         value={searchTerm}
         onChange={(e) => setSearchTerm(e.target.value)}
       />
       <button type="submit">SEARCH</button>
     </form>
   </div>

  <div>{questions.map(question=>{return <FeedPost key={question._id} 
    questionId={question._id}
    name = {question.name}
    title={question.title} 
    body={question.body}
    tags={question.tags}
    createdAt={question.createdAt}
    answers={question.answers}/>})}</div>
    </>
  );
};

export default Feeds;
