import React from 'react'
import axios from 'axios';
import { useState } from 'react';
import { useSelector } from 'react-redux/es/hooks/useSelector';
function Registration() {

    const [files, setFiles] = useState([]);
    const id = useSelector((state)=>state.auth.user._id);
    const token = useSelector((state)=>state.auth.token);


    const handleFileChange = (event) => {
      setFiles(event.target.files);
    };

    const handleUpload = async () => {
      try {
        const formData = new FormData();

        formData.append('id',id);


        for (let i = 0; i < files.length; i++) {
          formData.append('pdf', files[i]);
        }

        await axios.post(`http://localhost:3001/${id}/upload`, formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
            'Authorization':token
          },
        });

        // Handle success, e.g., show a success message
        console.log('Files uploaded successfully');
      } catch (error) {
        // Handle error, e.g., show an error message
        console.error('Error uploading files:', error.message);
      }
    };

    return (
      <div className='center-container-upload inline-element'>

        <h1 className='inline-element upload-title mb-5'>Select Relevant PDF files(Can select upto 5 files):</h1>
          <br/>
          <input className='inline-element mb-4' type="file" multiple onChange={handleFileChange} />
        <br />
        <button className="inline-element shadow__btn mb-3" onClick={handleUpload}>Upload</button>
        <div className=''>
        <div aria-label="inline-element mb-3 Orange and tan hamster running in a metal wheel" role="img" class="wheel-and-hamster">
	<div class="wheel"></div>
	<div class="hamster">
		<div class="hamster__body">
			<div class="hamster__head">
				<div class="hamster__ear"></div>
				<div class="hamster__eye"></div>
				<div class="hamster__nose"></div>
			</div>
			<div class="hamster__limb hamster__limb--fr"></div>
			<div class="hamster__limb hamster__limb--fl"></div>
			<div class="hamster__limb hamster__limb--br"></div>
			<div class="hamster__limb hamster__limb--bl"></div>
			<div class="hamster__tail"></div>
		</div>
	</div>
	<div class="spoke"></div>
</div>
        </div>
      </div>
    );

}

export default Registration
