import React, { useState } from 'react'
import axios from 'axios';
import { useEffect } from 'react';
import {  useSelector } from 'react-redux/es/hooks/useSelector';
import FeedPost from '../widgets/feedPost';
const MyQuestions= () =>{
    const name = useSelector((state)=>state.auth.user.name);
    const [myQuestions, setMyQuestions] = useState([]);
    const token = useSelector((state)=>state.auth.token)
    useEffect(()=>{
        const showMyQuestions = async()=>{
            try {
              const response = await axios.get(`http://localhost:3001/questions/${name}`,{
                headers:{
                  'Authorization':token
                }
              });
              setMyQuestions(response.data);
              console.log(myQuestions)
            } catch (error) {
              console.log(error);
            }
          };
          showMyQuestions();

    },[])

  return (
    <div>
        {myQuestions.map(question=>{return <FeedPost key={question._id} 
        questionId={question._id}
        name = {question.name}
        title={question.title} 
        body={question.body}
        tags={question.tags}
        createdAt={new Date(question.createdAt)}
        answers={question.answers}/>})
}
<div class="cube-loader">
  <div class="cube-top"></div>
  <div class="cube-wrapper">
    <span style="--i:0" class="cube-span"></span>
    <span style="--i:1" class="cube-span"></span>
    <span style="--i:2" class="cube-span"></span>
    <span style="--i:3" class="cube-span"></span>
  </div>
</div>
</div>
  )}; 

export default MyQuestions
