import React from 'react'
import Navbar from '../widgets/navbar'
import axios from 'axios';
import { useState } from 'react';
import { useSelector } from 'react-redux';

function Profile() {
    const userId = useSelector((state)=>state.auth.user._id);
    const name = useSelector((state)=>state.auth.user.name);
    const email = useSelector((state)=>state.auth.user.email);
    const occupation = useSelector((state)=>state.auth.user.occupation);

    const [userData, setUserData] = useState({
        name: name,
        email: email,
        occupation: occupation,
        profileImage: null,
      });

      const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUserData({ ...userData, [name]: value });
      };

      const handleFileChange = (e) => {
        const file = e.target.files[0];
        setUserData({ ...userData, profileImage: file });
      };

      const handleUpdateProfile = async () => {
        const formData = new FormData();

        Object.keys(userData).forEach((key) => {
          formData.append(key, userData[key]);
        });

        try {
          await axios.put(`http://localhost:3000/users/${userId}`, formData, {
            headers: {
              'Content-Type': 'multipart/form-data',
            },
          });

          console.log('User profile updated successfully!');
        } catch (error) {
          console.error('Error updating user profile:', error);
        }
      };

  return (
    <>
    <Navbar/>

    {/* Update Section */}


  return (
    <div className='update-container center-container-update'>

      <h2 className='upload-title mb-5'>Update Profile</h2>
      <label className='update-label'>Profile Image:</label>
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <div
          style={{
            width: '50px',
            height: '50px',
            borderRadius: '50%',
            overflow: 'hidden',
            marginRight: '10px',
          }}
        >
          {userData.profileImage && (
            <img
              src={URL.createObjectURL(userData.profileImage)}
              alt="Profile"
              style={{ width: '100%', height: '100%', objectFit: 'cover' }}
            />
          )}
        </div>
        <label
          htmlFor="profileImageInput"
          style={{
            cursor: 'pointer',
            padding: '10px',
            backgroundColor: '#007bff',
            color: 'white',
            borderRadius: '5px',
          }}
        >
          Choose Image
        </label>
        <input
          type="file"
          id="profileImageInput"
          name="profileImage"
          style={{ display: 'none' }}
          onChange={handleFileChange}
        />
      </div>
      <br/>
      <label className='update-label'>Name :  </label>
      <input className='search-input' type="text" name="name" value={userData.name} onChange={handleInputChange} />
      <br />

      <label className='update-label'>Email :  </label>
      <input className='search-input' type="text" name="email" value={userData.email} onChange={handleInputChange} />
      <br />



      <label className='update-label'>Occupation :  </label>
      <input type="text" name="occupation" value={userData.occupation} onChange={handleInputChange} />
      <br />





      <button onClick={handleUpdateProfile}>Update Profile</button>
    </div>
  );
    </>
  )
}

export default Profile
