import React, { useState } from 'react';
import axios from 'axios';
import { useSelector } from 'react-redux/es/hooks/useSelector';

const AddQuestion = () => {

const userId = useSelector((state)=>state.user._id);  
const token = useSelector((state)=>state.user.token);
  const [questionData, setQuestionData] = useState({
    title: '',
    body: '',
    tags: '',
    createdBy: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setQuestionData({ ...questionData, [name]: value });
  };

  const handleAddQuestion = async () => {
    try {
      setQuestionData({...questionData,createdBy:userId});  
      console.log(questionData)
      const response = await axios.post('http://localhost:3001/question/', questionData );

      // Handle the response as needed (e.g., show a success message, reset form)
      console.log('Question added successfully:', response.data);

      // Reset the form
      setQuestionData({
        title: '',
        body: '',
        tags: '',
        createdBy:''
      });
    } catch (error) {
      console.error('Error adding question:', error);
      // Handle error (e.g., show an error message)
    }
  };

  return (
    <div className="max-w-md mx-auto bg-white p-8 shadow-md rounded-md">
  <h2 className="text-2xl font-semibold mb-4">Add a New Question</h2>
  <form>
    <div className="mb-4">
      <label className="block text-sm font-medium text-gray-600">Title:</label>
      <input
        type="text"
        name="title"
        value={questionData.title}
        onChange={handleInputChange}
        className="mt-1 p-2 w-full border rounded-md"
      />
    </div>
    <div className="mb-4">
      <label className="block text-sm font-medium text-gray-600">Body:</label>
      <textarea
        name="body"
        value={questionData.body}
        onChange={handleInputChange}
        className="mt-1 p-2 w-full border rounded-md"
      ></textarea>
    </div>
    <div className="mb-4">
      <label className="block text-sm font-medium text-gray-600">Tags (comma-separated):</label>
      <input
        type="text"
        name="tags"
        value={questionData.tags}
        onChange={handleInputChange}
        className="mt-1 p-2 w-full border rounded-md"
      />
    </div>
    <button
      type="button"
      onClick={handleAddQuestion}
      className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600"
    >
      Add Question
    </button>
  </form>
</div>

  );
};

export default AddQuestion;
