import React from 'react'
import Navbar from '../widgets/navbar'
import axios from 'axios';
import { useState } from 'react';
import { useSelector } from 'react-redux';

function Profile() {
    const userId = useSelector((state)=>state.auth.user._id);
    const name = useSelector((state)=>state.auth.user.name);
    const email = useSelector((state)=>state.auth.user.email);
    const occupation = useSelector((state)=>state.auth.user.occupation);

    const [userData, setUserData] = useState({
        name: name,
        email: email,
        occupation: occupation,
        isMentor: false,
        profileImage: null,
      });

      const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUserData({ ...userData, [name]: value });
      };

      const handleFileChange = (e) => {
        const file = e.target.files[0];
        setUserData({ ...userData, profileImage: file });
      };

      const handleUpdateProfile = async () => {
        const formData = new FormData();

        Object.keys(userData).forEach((key) => {
          formData.append(key, userData[key]);
        });

        try {
          await axios.put(`http://localhost:3000/users/${userId}`, formData, {
            headers: {
              'Content-Type': 'multipart/form-data',
            },
          });

          console.log('User profile updated successfully!');
        } catch (error) {
          console.error('Error updating user profile:', error);
        }
      };

  return (
    <>
    <Navbar/>

    {/* Update Section */}


  return (
    <div className='update-container'>
      <h2>Update Profile</h2>
      <label>Name:</label>
      <input type="text" name="name" value={userData.name} onChange={handleInputChange} />
      <br />

      <label>Email:</label>
      <input type="text" name="email" value={userData.email} onChange={handleInputChange} />
      <br />



      <label>Occupation:</label>
      <input type="text" name="occupation" value={userData.occupation} onChange={handleInputChange} />
      <br />

      <label>Is Mentor:</label>
      <input type="checkbox" name="isMentor" checked={userData.isMentor} onChange={handleInputChange} />
      <br />

      <label>Profile Image:</label>
      <input type="file" name="profileImage" onChange={handleFileChange} />
      <br />

      <button onClick={handleUpdateProfile}>Update Profile</button>
    </div>
  );
    </>
  )
}

export default Profile
