  const [answer, setAnswer] = useState('');

  const toggleDiv = () => {
    setIsOpen(!isOpen);
  };

  const handleInputChange = (e) => {
    setAnswer(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Perform any action with the entered answer
