import React, { useState } from 'react';
import axios from 'axios';
import { useSelector } from 'react-redux';
import logo from '../Logo/LOGO1.png'
function MentorPage() {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
const token = useSelector((state)=>state.auth.token);
  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:3001/searchMentor?query=${searchQuery}`,{
        headers:{
            'Authorization':token
        }
      });
      setSearchResults(response.data);
    } catch (error) {
      console.error('Error searching users:', error);
    }
  };

  return (
    <div>
      <h1>Mentor Page</h1>
      <input
        type="text"
        placeholder="Search by occupation"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />

      <div>
        <h2>Search Results</h2>
        {Array.isArray(searchResults) && searchResults.map((user) => (
          <div className='center-container' key={user._id}>
            <div className="card w-75 text-center mb-1">
              <div style={{ position: 'absolute', top: '10px', right: '10px' }}>
                <img
                  src={logo}  // Replace with the actual path to your logo image
                  alt="Logo"
                  style={{ width: '70px', height: '70px' }}
                />
                <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', flexDirection: 'column' }}>
                  {user.profileImage && (
                    <img
                      src={`http://localhost:3001${user.profileImage}`}
                      alt="Profile"
                      style={{ width: '70px', height: '70px', borderRadius: '50%' }}
                    />
                  )}
                  <h3 className='font-weight-bold text-uppercase'>{user.name}</h3>
                </div>
                <div className="card-header font-weight-bold">{user.occupation}</div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );

}

export default MentorPage;
