  return (
    <div className='center-container'>
    <div className="card w-75 text-center mb-1">
      <h3 className='font-weight-bold text-uppercase'>{name}</h3>
  <div class="card-header font-weight-bold">
    {title}
  </div>
  <div className="card-body">
    <h5 className="card-title"Name>{body}</h5>
    <p className="card-text">{answers.map(answer => answer.body)}</p>
    <button onClick={handleFullPost} className="btn btn-link mb-1">show full post..</button>
    <div>
    <button onClick={toggleDiv} className="btn btn-success">Add an Answer</button>
    {isOpen && (
        <div className="slide-down-container">
          <form >
            <label>
              Enter your answer:
              <textarea type="text" value={answer} onChange={handleInputChange} />
            </label>
            <button onClick={handleAnswer} className='btn btn-primary'>Submit</button>
          </form>
        </div>
      )}
    </div>
  </div>

    <div>
      {tags.map(tag => <a href="#" class="badge badge-secondary m-1">{tag}</a> )}
    </div>

  <div class="card-footer text-muted">
    {createdAt}
  </div>
</div>  
</div>
  )
}

export default FeedPost;
