import React, { useEffect, useState } from 'react';
import FeedPost from './feedPost';
import axios from 'axios';
import { useSelector } from 'react-redux/es/hooks/useSelector';
const Feeds = () => {

  const [questions, setQuestions] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [myQuestions, setMyQuestions] = useState([]);
  const [isMyQuestions, setIsMyQuestions] = useState(false);
  const token = useSelector((state)=>state.auth.token);
  const name = useSelector((state)=>state.auth.user.name);


  const search = async () => {
    try {
      console.log(searchTerm)
      const response = await axios.get(`http://localhost:3001/${searchTerm}/search/`,{
        headers:{
          'Authorization':token
        }
      } );
      setSearchResults(response.data);
      setQuestions(searchResults);
      console.log(questions)
    } catch (error) {
      console.error('Error fetching search results:', error);
    }
  };
  const getQuestions=async()=>{
    try {
      const response = await fetch('http://localhost:3001/getQuestions/',{
        headers:{
          'Authorization': token
        }
      });
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      const data = await response.json();

      setQuestions(data);
    } catch (error) {
      console.error('Error fetching items:', error);

    } 
  }
  const showMyQuestions = async()=>{
    try {
      const response = await axios.get(`http://localhost:3001/questions/${name}`,{
        headers:{
          'Authorization':token
        }
      });
      setMyQuestions(response.data);
      setIsMyQuestions(true);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(()=>{
    getQuestions();
  },[questions]);
if(searchTerm.length !==0 ){
 return(
  <>
   {/* Question Search Box */}
   <button type='button' className='adjust fixed-button-2 btn btn-primary' onClick={showMyQuestions}>My Questions</button>

   <div className='center-search-container'>


       <input
         type="text"
         className='search-input'
         placeholder="Search a question"
         value={searchTerm}
         onChange={(e) => {setSearchTerm(e.target.value); search();}}
       />


   </div>

  <div>{searchResults.map(question=>{return <FeedPost key={question._id} 
    questionId={question._id}
    name = {question.name}
    title={question.title} 
    body={question.body}
    tags={question.tags}
    createdAt={new Date(question.createdAt)}
    answers={question.answers}/>})
  }
  </div>
    </>
  );
}
else if(isMyQuestions){
  return(
    <>
     {/* Question Search Box */}
     <button type='button' className='adjust fixed-button-2 btn btn-primary' onClick={showMyQuestions}>My Questions</button>

     <div className='center-search-container'>


         <input
           type="text"
           className='search-input'
           placeholder="Search a question"
           value={searchTerm}
           onChange={(e) => {setSearchTerm(e.target.value); search();}}
         />


     </div>

    <div>{myQuestions.map(question=>{return <FeedPost key={question._id} 
      questionId={question._id}
      name = {question.name}
      title={question.title} 
      body={question.body}
      tags={question.tags}
      createdAt={new Date(question.createdAt)}
      answers={question.answers}/>})
    }
    </div>
      </>
    );
}
else{
  return(
    <>
     {/* Question Search Box */}
     <button type='button' className='adjust fixed-button-2 btn btn-primary' onClick={showMyQuestions}>My Questions</button>

     <div className='center-search-container'>


         <input
           type="text"
           className='search-input'
           placeholder="Search a question"
           value={searchTerm}
           onChange={(e) => {setSearchTerm(e.target.value); search();}}
         />


     </div>

    <div>{questions.map(question=>{return <FeedPost key={question._id} 
      questionId={question._id}
      name = {question.name}
      title={question.title} 
      body={question.body}
      tags={question.tags}
      createdAt={new Date(question.createdAt)}
      answers={question.answers}/>})
    }
    </div>
      </>
    );
}
};

export default Feeds;

